mapper.update:
update t_inter
modify_date = #{modifyDate}, module_id = #{moduleId}, name = #{name}, path = #{path}, method = #{method}, scheme = #{scheme}, summary = #{summary}, description = #{description}, consume = #{consume}, produce = #{produce}, deprecated = #{deprecated}, sort_weight = #{sortWeight},
where id = #{id} and doc_id = #{docId}

mapper.delete:
delete from
	t_inter
where
	id = #{interId}
and
	doc_id = #{docId}

mapper.sql:
from t_inter i left join t_module m on i.module_id = m.id where i.doc_id = #{docId}
and i.module_id = #{moduleId}
and i.description like #{description}
and i.name like #{name}

mapper.select:
select i.* from t_inter i left join t_module m on i.module_id = m.id where i.doc_id = #{docId}
and i.deprecated = #{deprecated}
order by m.sort_weight asc,i.sort_weight asc,create_date asc

select
	*
from
	t_inter
where
	doc_id = #{docId}
and
	path = #{path}
and
	method = #{method}
