mapper.update:
update t_module
modify_date = #{modifyDate}, code = #{code}, name = #{name}, description = #{description}, sort_weight = #{sortWeight},
where id = #{id} and doc_id = #{docId}

mapper.delete:
delete from
	t_module
where
	id = #{moduleId}
and
	doc_id = #{docId}

mapper.sql:
from
	t_module
where
	doc_id = #{docId}

and
	code like #{code}



and
	name like #{name}



and
	description like #{description}

mapper.select:
select *
order by sort_weight asc,create_date asc
limit #{pager.start},#{pager.pageSize}
